mapper.insert:
INSERT INTO account (userId, coinId, accountStatus, createUser, createTime, updateUser, updateTime
) VALUES (
#{userId}, #{coinId}, #{accountStatus}, #{createUser}, #{createTime}, #{updateUser}, #{updateTime}
)

mapper.update:
UPDATE account SET
netValue = #{netValue},
updateTime = #{updateTime}
WHERE userId = #{userId} AND coinId = #{coinId}

UPDATE account SET
carryingAmount = #{carryingAmount},
netValue = #{netValue},
updateUser = #{updateUser},
updateTime = #{updateTime}
WHERE userId = #{userId} AND coinId = #{coinId}
